import React,{useState}from "react";
import Navbar from "./Navbar";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import FoodList from "./FoodList";
import About from "./About";
import Footer from "./Footer";
import BurgerDetails from "./BurgerDetails";
import Review from "./Review";


function App({reviews, setReviewList}) {
  const [burgerList, setBurgerList] = useState(null);

function onDetailsClick(clickedBurger){
  setBurgerList(clickedBurger)
}
  // const [query, setQuery] = useState("");
  // const [searchParam] = useState(["name"]);

  return (
    <Router>
      <div>
        <Navbar />
      </div>
      <div className="row mt-3">
        <Routes>
          <Route
            exact
            path="/"
            element={<FoodList onDetailsClick = {onDetailsClick} />}
          ></Route>
          <Route exact path="/about" element={<About />}></Route>
          <Route
            exact
            path="/review"
            element={<Review reviewList={reviews} setReviewList={setReviewList} />}
          ></Route>
          <Route
            exact
            path="/burgers/:burgerId"
            element={<BurgerDetails />}
          ></Route>
        </Routes>
      </div>
      <Footer />
    </Router>
  );
}

export default App;
