import React from 'react'

const SignUpForm = () => {
  // const [product_name, setProductName] = useState('');
  //   //const [quantity, setQuantity] = useState('');
  //   const [unit_price, setUnitPrice] = useState('');
  //   //const [supplier_name, setUnitSupplierName] = useState('');
  //   const [description, setDescription] = useState('');
  //   const [onUploadImage, setonUploadImage] = useState();
    //const history = useHistory();

    const handleSubmit = (e) => {
        e.preventDefault();
        const sale = { 
            product_name:product_name,
            description:description,
            unit_price:unit_price,
            image_url :onUploadImage,
        };

        fetch('http://localhost:9292/products', {
            method: 'POST',
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(sale)
        }) .then((response) => response.json())
        .then((product) => { 
            handleAddNewProduct(product);
        })
    }
  return (
<div className="about">
      <div className="text">
        <center>
      <h1> <span style={{fontweight:"bold", color:"white"}}>Restaurant</span> <span style={{fontweight:"bold", color:"white"}}>APP</span></h1>
        </center>
        </div>
        <div className="create">
            <h2>SIGNUP</h2>
            <form onSubmit={handleSubmit}>
                <label>Image</label>
                    <input
                        className="form-control"
                        type="text"
                         name="image"
                        value={onUploadImage}
                        onChange={(e) =>  setonUploadImage(e.target.value)}
                        placeholder="Enter image url here..."/>

                <label>Product Title</label>
                <input 
                    type="text"
                    required
                    value={product_name}
                    onChange={(e) => setProductName(e.target.value)}
                />
                <label>Product Price:</label>
                <input 
                    type="text"
                    required
                    value={unit_price}
                    onChange={(e) => setUnitPrice(e.target.value)}
                />
                <label>Product description:</label>
                <textarea
                    required
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                {<button>Add Product</button>}
            </form>
        </div>
    </div>

  )
}

export default SignUpForm
