import React, {useParams, useEffect, useState} from 'react';

const SEARCH_MEAL = "http://localhost:5000/burgers"

export default function BurgerDescription(){
    const[image_url, setImage_url] = useState("")
    const[name, setName] = useState("")
    const[price, setPrice] = useState("")

    let {burgerId} = useParams()

    const actualSearch = SEARCH_MEAL + burgerId

    const mealFetcher = () => {
        fetch(actualSearch)
            .then((res) => res.json())
            .then((data) => {
                const meal = data.burgers[0]

                setTitle(meal.strMeal)
                setCategory(meal.strCategory)
                setInstructions(meal.strInstructions)
                setImage(meal.strMealThumb)

            })
    }

    useEffect(() => {
        fetch(actualSearch)
        .then(response => response.json())
        .then((burgers) => {

            burgers.map((burger) => {
                return setName(burger.name), setImage_url(burger.image_url), setPrice(burger.price);
            })
        })
      }, [])

return(
    <div className="text-bg-info p-3 " >
    <div className='container'>
        <center>
        <div className='card' style={{width : 25 + 'rem'}} >
        <img src={image_url} style={{height: 300 + 'px', width: 300 + 'px', }} alt={name}
         />
            <h5>Burger:{name}</h5>
            <p>burgerThumbnail:{image_url}</p>
            <p>Burger Price:{price}</p>
        </div>
        </center>
    </div>
    </div>
)
}



